import { useState } from 'react';
import axios from 'axios';
import './App.css';


function App() {
  const [form,setForm]=useState({text:"",ttl:1,end:null,max:1,limit:null});
  const [g,setG]=useState(null);
   const [content, setContent] = useState("");
  const [ttl, setTtl] = useState("");
  const [maxViews, setMaxViews] = useState("");
  const [link, setLink] = useState("");

  const post1=async(e)=>
  {
    e.preventDefault();
     try {
      const res = await axios.post("http://localhost:4000/api/pastes", {
        content,
        ttl_seconds: ttl ? Number(ttl) : undefined,
        max_views: maxViews ? Number(maxViews) : undefined,
      });

      setLink(res.data.url);
      setContent("");
      setTtl("");
      setMaxViews("");
    } catch (err) {
      alert("Error creating paste");
    }



  };

  return (
    <div className="App">

     <div className="text">
   <form onSubmit={post1} >
   <input type='text' value={form.text} onChange={(e)=>setForm({...form,text:e.target.value})}/>
   <input type='number' value={form.ttl} onChange={(e)=>setForm({...form,ttl:e.target.value})}/>

   <input type='number' value={form.max} onChange={(e)=>setForm({...form,max:e.target.value})}/>

   <button type='submit'>submit</button>
   </form>
     </div>
     <div className="link">
       <h1>{g}</h1>
     </div>
    </div>
  );
}

export default App;
